import "core-js/modules/es.array.push.js";
export default {
  mounted() {
    const token = localStorage.getItem('token');
    if (!token) {
      this.$router.push('/login');
    }
  },
  methods: {
    async logout() {
      try {
        const response = await fetch('http://localhost:3000/logout', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
            // You may also need to include authentication headers if necessary
          }
        });
        if (response.ok) {
          // Redirect to login page or perform any other necessary actions upon successful logout
          // For example, you can clear local storage and redirect to the login page
          localStorage.removeItem('token');
          this.$router.push('/login');
        } else {
          console.error('Failed to logout');
        }
      } catch (error) {
        console.error('Error logging out:', error);
      }
    }
  }
};
